import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { SketchPicker } from 'react-color';
import Draggable from 'react-draggable';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
    const [board, setBoard] = useState([]);
    const [currentColor, setCurrentColor] = useState("#000000");
    const [selectedPixel, setSelectedPixel] = useState(null);
    const [showPicker, setShowPicker] = useState(false);
    const [pickerPosition, setPickerPosition] = useState({ x: 0, y: 0 });
    const [tempBoard, setTempBoard] = useState([]);
    const [pickerKey, setPickerKey] = useState(0); // 팔레트 위치를 강제로 업데이트하기 위한 상태
    const [pickerScale, setPickerScale] = useState(1); // 팔레트 스케일

    useEffect(() => {
        socket.on('initial_board', board => {
            setBoard(board);
            setTempBoard(board);
        });

        socket.on('change_color', data => {
            setBoard(prevBoard => {
                const { x, y, color } = data;
                const newBoard = [...prevBoard];
                newBoard[y][x] = color;
                return newBoard;
            });
            setTempBoard(prevBoard => {
                const { x, y, color } = data;
                const newBoard = [...prevBoard];
                newBoard[y][x] = color;
                return newBoard;
            });
        });

    }, []);

    const handlePixelClick = (x, y, event) => {
        if (showPicker) return; // 팔레트가 이미 열려 있으면 다른 픽셀 클릭 방지

        setSelectedPixel({ x, y });
        setShowPicker(true);

        // 팔레트 위치를 픽셀 위치에서 약간 떨어진 곳으로 설정
        setPickerPosition({ x: event.clientX + 20, y: event.clientY + 20 });
        setPickerKey(prevKey => prevKey + 1); // 새로운 키 값 설정

        // 화면 확대 비율에 따라 팔레트 스케일 조정
        const scale = 1 / window.devicePixelRatio;
        setPickerScale(scale);
    };

    const handleColorChange = (color) => {
        setCurrentColor(color.hex);
        if (selectedPixel) {
            const { x, y } = selectedPixel;
            setTempBoard(prevBoard => {
                const newBoard = prevBoard.map(row => [...row]);
                newBoard[y][x] = color.hex;
                return newBoard;
            });
        }
    };

    const confirmColorChange = () => {
        if (selectedPixel) {
            const { x, y } = selectedPixel;
            socket.emit('change_color', { x, y, color: currentColor });
            setSelectedPixel(null);
            setShowPicker(false);
        }
    };

    const cancelColorChange = () => {
        setSelectedPixel(null);
        setShowPicker(false);
        setTempBoard(board); // 원래 보드 상태로 되돌리기
    };

    return (
        <div className="App">
            <h1>픽셀을 클릭하여 색상 선택하기</h1>
            {showPicker && (
                <Draggable key={pickerKey} defaultPosition={pickerPosition}>
                    <div style={{ position: 'absolute', zIndex: 1000, background: '#fff', padding: '10px', borderRadius: '8px', boxShadow: '0 0 10px rgba(0,0,0,0.2)', transform: `scale(${pickerScale})`, transformOrigin: 'top left' }}>
                        <SketchPicker color={currentColor} onChange={handleColorChange} />
                        <div style={{ marginTop: '10px', textAlign: 'center' }}>
                            <button onClick={confirmColorChange} style={{ marginRight: '10px' }}>확인</button>
                            <button onClick={cancelColorChange}>취소</button>
                        </div>
                    </div>
                </Draggable>
            )}
            <div style={{ display: 'flex', flexWrap: 'wrap', flexDirection: 'row' }}>
                {tempBoard.map((row, y) => (
                    <div key={y} style={{ display: 'flex' }}>
                        {row.map((color, x) => (
                            <div key={x} onClick={(e) => handlePixelClick(x, y, e)} 
                                 style={{
                                    width: '20px',
                                    height: '20px',
                                    backgroundColor: color,
                                    border: selectedPixel && selectedPixel.x === x && selectedPixel.y === y ? '2px solid red' : '1px solid black',
                                    boxSizing: 'border-box' // border-box를 설정하여 border가 크기에 영향을 미치지 않게 함
                                 }} />
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
