import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
    const [board, setBoard] = useState([]);
    const [pixelSize, setPixelSize] = useState(20); // 초기 픽셀 크기

    useEffect(() => {
        socket.on('initial_board', board => {
            setBoard(board);
        });

        const handleResize = () => {
            const size = Math.max(5, Math.min(20, window.innerWidth / 100));
            setPixelSize(size);
        };

        window.addEventListener('resize', handleResize);
        handleResize(); // 초기 로드 시 크기 조정

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const changeColor = (x, y) => {
        const color = prompt("Enter a hex color code:", "#000000");
        socket.emit('change_color', { x, y, color });
    };

    return (
        <div className="App">
            <h1>React Pixel Board</h1>
            <div style={{ display: 'flex', flexDirection: 'column', maxWidth: '100%', overflow: 'auto' }}>
                {board.map((row, y) => (
                    <div key={y} style={{ display: 'flex' }}>
                        {row.map((color, x) => (
                            <div key={x} onClick={() => changeColor(x, y)} 
                                 style={{ width: `${pixelSize}px`, height: `${pixelSize}px`, backgroundColor: color, border: '1px solid black' }} />
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
