import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
    const [board, setBoard] = useState([]);

    useEffect(() => {
        socket.on('initial_board', board => {
            setBoard(board);
        });

        socket.on('change_color', data => {
            setBoard(prevBoard => {
                const { x, y, color } = data;
                const newBoard = [...prevBoard];
                newBoard[y][x] = color;
                return newBoard;
            });
        });

        return () => socket.disconnect();
    }, []);

    const changeColor = (x, y) => {
        const color = prompt("Enter a hex color code:", "#000000");
        socket.emit('change_color', { x, y, color });
    };

    return (
        <div className="App">
            <h1>React Pixel Board</h1>
            <div style={{ display: 'flex', flexDirection: 'column' }}>
                {board.map((row, y) => (
                    <div key={y} style={{ display: 'flex' }}>
                        {row.map((color, x) => (
                            <div key={x} onClick={() => changeColor(x, y)} style={{ width: '100px', height: '100px', backgroundColor: color, border: '1px solid black' }} />
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
